{-# LANGUAGE BangPatterns      #-}
{-# LANGUAGE OverloadedStrings #-}
{-# LANGUAGE TypeApplications  #-}

{- | The `flat` files containing validation scripts in `plutus-benchmarks` were
generated in August 2021.  In May 2022 it was observed that deserialising them
to memory and then reserialising them usually caused the size of the `flat`
files to increase, apparently because the CBOR encoding of `Data` objects had
changed.  The serialised versions show significant differences (see `testData`
below) but deserialise to identical objects.  These tests contain pairs of
different encodings of Data objects from a selection of the scripts and check
that they continue to deserialise to the same thing. -}

module CBOR.Stability

where

import PlutusCore.Data

import Codec.Serialise (deserialise)
import Data.ByteString.Lazy qualified as BSL (fromStrict, length)
import Data.Maybe (fromJust)
import Data.Text (Text)
import Text.Hex (decodeHex)
import Text.Printf (printf)

import Test.Tasty
import Test.Tasty.HUnit

tests :: TestTree
tests = testGroup "CBOR"
        [
         testGroup "stability" (fmap maketest testData)
        ]

-- | Convert the Text objects in the testData entries to bytestrings and then
-- deserialise them and check that both give the same Data object. Without the
-- bangs error messages can appear in the wrong place.
maketest :: (String, Text, Text) -> TestTree
maketest (name, s1, s2) =
    testCaseSteps name $ \step -> do
      let !bs1 = BSL.fromStrict . fromJust . decodeHex $ s1
          size1 = BSL.length bs1
      step $ printf "Deserialising Data object 1 for %s from bytestring (%s)" name (numBytes size1)
      let !data1 = deserialise @Data bs1
      let !bs2 = BSL.fromStrict . fromJust . decodeHex $ s2
          size2 = BSL.length bs2
      step $ printf "Deserialising Data object 2 for %s from bytestring (%s)" name (numBytes size2)
      let !data2 = deserialise @Data bs2
      step $ printf "Size difference: %s" (numBytes (size2 - size1))
      step "Checking Data for equality"
      assertEqual "Deserialisation produced different objects" data1 data2
          where numBytes s = if s == 1
                             then "1 byte"
                             else printf "%d bytes" s :: String

